import { Component } from '@angular/core';

import { StockListService } from './Services/stock-list.service';
import { subscribeOn } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'Stock-Market-UI';

  constructor(private StockListService: StockListService) {}

  globalStockList :any 
  stockList: any;
  userInput:any;

  ngOnInit() {
    this.StockListService.getStockLlist().subscribe((response) => {
      this.globalStockList = response?.results.slice(0, 50);
      this.stockList = this.globalStockList;
      console.log('response', this.stockList);
    });
  }

  getDaataAsUserInserted() {
    console.log('User input:', this.userInput);
    this.stockList = this.globalStockList
    this.stockList =  this.stockList?.slice(0, this.userInput);
  }
}

<div class="container">
    <h1>Stock List</h1>
  <div class="row">
    <div class="col-8">
      <input type="text" class="form-control" [(ngModel)]="userInput" placeholder="Enter your text">
    </div>
    <div class="col-4">
      <button type="button" class="btn btn-primary float-right" (click)="getDaataAsUserInserted()">Refresh Data</button>
    </div>
  </div>
  <table class="table table-striped">
    <thead>
      <tr>
        <th>Exchange Symbol</th>
        <th>Today's High Price</th>
        <th>Todays's Low price</th>
        <th>Today's volume</th>
        <th>Today's open</th>
        <th>Today's close</th>
        <!-- Add more table headers based on your API data -->
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let stock of stockList">
        <td>{{ stock?.T }}</td>
        <td>{{ stock?.h }}</td>
        <td>{{ stock?.l }}</td>
        <td>{{ stock?.v }}</td>

        <!-- Add more table columns based on your API data -->
      </tr>
    </tbody>
  </table>
</div>
